//import React, { Component } from 'react';
import React, { useState, useEffect } from 'react';


const App = () => {

    const [term, setTerm] = useState('');
    const [movieData, setMovieData] = useState([]);
    const [nextPageNum, setNextPageNum] = useState(1);
    const [movieInfos, setMovieInfos] = useState([]);

    const updateTerm = (e) => {
      setTerm(e.target.value);
    }

    const handleSubmit = (e) => {
      e.preventDefault();
      searchMovies(term);
    }

    const searchMovies = async (term) => {
        const res = await fetch(`/search/${term}`)
        if (res.ok) {
            const data = await res.json();
            return data;
        }
        throw res;
    }

    // const fetchMovies = async () => {
    //     const res = await fetch('/test')
    //     if (res.ok) {
    //         const data = await res.json();
    //         console.log(data);
    //         setMovies(data);
    //         return data;
    //     }
    //     throw res;
    // }

    const fetchMovieData = async (pageNumber) => {
      const res = await fetch(`/test?page=${pageNumber}`)
        if (res.ok) {
            const data = await res.json();
            console.log(data);
            setMovieData((data, null, 2) || 'No Movie');
            return data;
        }
        throw res;
    }

    const fetchNextMovie = () => {
        const data = fetchMovieData(nextPageNum);
        if(data === undefined) return;
        const newMovies = [
          ...movieInfos,
          data,
        ]
        setMovieInfos(newMovies);
        setNextPageNum(data.page + 1);
    }


    useEffect(() => {
        fetchNextMovie();
    }, []);


    return (
        <div>
          <h1>Hello, This Full Stack AI Challenge.</h1>
          <button onClick={() => {
              fetchNextUser();
            }}> Fetch Next User
          </button>
          <pre>{(movieData, undefined, '  ')}</pre>

        </div>
    );


}

export default App;
