//import React, { Component } from 'react';
import React, { useState, useEffect } from 'react';


const App = () => {

    const [term, setTerm] = useState('');
    const [moviesJSON, setMoviesJSON] = useState([]);
    const [nextPageNum, setNextPageNum] = useState(1);
    const [movieInfos, setMovieInfos] = useState([]);

    const updateTerm = (e) => {
      setTerm(e.target.value);
    }

    const handleSubmit = (e) => {
      e.preventDefault();
      searchMovies(term);
    }

    const searchMovies = async (term) => {
        const res = await fetch(`/search/${term}`)
        if (res.ok) {
            const data = await res.json();
            return data;
        }
        throw res;
    }

    // const fetchMovies = async () => {
    //     const res = await fetch('/test')
    //     if (res.ok) {
    //         const data = await res.json();
    //         console.log(data);
    //         setMovies(data);
    //         return data;
    //     }
    //     throw res;
    // }

    const fetchMovieData = (pageNumber) => {
      return fetch(`/test?page=${pageNumber}`)
        .then( ( data ) => {
          console.log(data.json());
          return data.json();
        })
        .catch(err => {
          console.error(err);
        })
    }

    const fetchNextMovie = () => {
      fetchMovieData(nextPageNum).then(movieData => {
        setMoviesJSON(JSON.stringify(movieData, null, 2) || 'No Movie');
        if(movieData === undefined) return;
        const newMovies = [
          ...movieInfos,
          movieData,
        ]
        setMovieInfos(newMovies);
        setNextPageNum(movieData.page + 1);
      })
    }

    useEffect(() => {
        fetchNextMovie();
    }, []);


    return (
        <div>
          <h1>Hello, This Full Stack AI Challenge.</h1>
          <pre>{(moviesJSON, undefined, '  ')}</pre>
        </div>
    );


}

export default App;
